import React from 'react'
import { motion } from 'framer-motion'
import ImageModal from './ImageModal'

const ImageCard = (props) => {
    const imageThumb = () => {
        let thumbUrl = "https://i.imgur.com/1sYu3VL.png"
        if (props.image.thumb_url) {
            thumbUrl = props.image.thumb_url
        }

        return thumbUrl
    }

    const imageInfo = async () => {
        const imageData = await props.fetchImageData(props.image.id)
        console.log(imageData)
        return <ImageModal imageData={imageData}/>
    }

    return (
      <div>
        <motion.div
          className="uk-inline"
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.9 }}
        >
          <img
            className="uk-card uk-card-default uk-flex uk-flex-center uk-flex-middle"
            src={imageThumb()}
            alt={props.image.title}
            onClick={imageInfo}
            data-uk-img
          ></img>
          <div className="uk-overlay uk-overlay-primary uk-dark uk-position-bottom">
            <p>{props.image.title}</p>
          </div>
        </motion.div>
        <a
          className="uk-button uk-button-default"
          href="#modal-center"
          data-uk-toggle
        >
          Open
        </a>
        <div id="modal-center" className="uk" data-uk-modal>
          <div className="uk-modal-dialog uk-modal-body uk-margin-auto-vertical">
            <button
              className="uk-modal-close-default"
              type="button"
              data-uk-close
            ></button>
            <p>This should work but probably won't</p>
          </div>
        </div>
      </div>
    );
}

export default ImageCard;
