import React from 'react'
import { motion } from 'framer-motion'

const ImageCard = (props) => {
    const imageThumb = () => {
        let thumbUrl = "https://i.imgur.com/1sYu3VL.png"
        if (props.image.thumb_url) {
            thumbUrl = props.image.thumb_url
        }

        return thumbUrl
    }

    return (
      <div>
        <motion.div whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.9 }}>
          <div
            className="uk-card uk-card-default uk-flex uk-flex-center uk-flex-middle uk-background-cover uk-light"
                    style={{backgroundImage: imageThumb() }}
            alt={props.image.title}

            data-uk-img
                ></div>
        </motion.div>
        </div>
    );
}

export default ImageCard;
