import React from 'react'   
import ImageCardList from './ImageCardList'

export default class Search extends React.Component {

  constructor() {
    super()
    this.state = {
      images: [],
      imageId: null,
      image: []
    }
  }

  componentDidMount() {
    fetch("https://nasa-file-search-backend.herokuapp.com/search?query=earth&page=1&perPage=15")
      .then(resp => resp.json())
      .then(json => {
        this.setState({
          images: json.file
        });
      });
  }


  getImageInfo = (id) => {
    this.setState({
      imageId: id
    })

    fetch(`https://nasa-file-search-backend.herokuapp.com/search/${this.state.imageId}`)
      .then(resp => resp.json())
      .then(json => {
        console.log(json.file)
        this.setState({
          image: json.file
        });
      });
  }


    render() {
        return (
          <div className="uk-container uk-margin">
            <div className="uk-container uk-flex uk-flex-between uk-margin">
              <div className="uk-inline uk-margin">
                <form className="uk-form-width-medium uk-search uk-search-default">
                  <input
                    className="uk-search-input"
                    type="search"
                    placeholder="Search..."
                  ></input>
                </form>
              </div>

              <div className="uk-inline">
                <button className="uk-button uk-button-default" type="button" data-uk-toggle="target: #offcanvas-push">
                  Filter
                </button>
                <div id="offcanvas-push" data-uk-offcanvas="mode: push; overlay: true; flip: true">
                  <div className="uk-offcanvas-bar">
                    <button className="uk-offcanvas-close" type="button" data-uk-close></button>
                    <h1>This will be the options</h1>
                  </div>
                </div>
              </div>
            </div>
            <ImageCardList images={this.state.images} getImageId={this.getImageInfo} imageInfo={this.state.imageInfo}/>
          </div>
        );
    }
}
