import React from 'react'

class ImageModal extends React.Component {

    keywordsExist = () => {
        if (this.props.imageData.keywords) {
            return this.props.imageData.keywords.map((keyword) => <p>{keyword.phrase}</p>)
        }
    }

    render() {
        console.log(this.props.imageData)
        const { description, title, hrefs } = this.props.imageData;
        return (
        <div id="modal-center" className="uk-flex-top" data-uk-modal>
            <div className="uk-modal-dialog uk-modal-body uk-margin-auto-vertical" style={{borderRadius: "1.5rem"}}>
                    <button className="uk-modal-close-default" type="button" data-uk-close></button>
                    <img src={this.props.imagePhoto} style={{}}></img>
                    <h1>{title}</h1>
                    <div style={{ fontWeight: 'bold' }}>{this.keywordsExist()}</div>
                    <p>{description}</p>
            </div>
        </div>
        );

    }
}

export default ImageModal;
